import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLocalStorage } from '../hooks/useLocalStorage';
import './SearchBar.css';

const MAX_LENGTH = 120;

const SearchBar = ({
    initialValue = '',
    autoFocus = false,
    onSearch,
    size = 'large',
    showError = true,
}) => {
    const [query, setQuery] = useState(initialValue);
    const [error, setError] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [selectedIndex, setSelectedIndex] = useState(-1);
    const [recentSearches, setRecentSearches] = useLocalStorage('recent-searches', []);

    const inputRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        setQuery(initialValue);
    }, [initialValue]);

    useEffect(() => {
        if (autoFocus && inputRef.current) {
            inputRef.current.focus();
        }
    }, [autoFocus]);

    // Filter suggestions based on query
    useEffect(() => {
        if (query.trim() && recentSearches.length > 0) {
            const filtered = recentSearches
                .filter(s => s.toLowerCase().includes(query.toLowerCase()))
                .slice(0, 5);
            setSuggestions(filtered);
        } else if (!query.trim()) {
            setSuggestions(recentSearches.slice(0, 5));
        } else {
            setSuggestions([]);
        }
    }, [query, recentSearches]);

    const saveSearch = (searchQuery) => {
        const updated = [searchQuery, ...recentSearches.filter(s => s !== searchQuery)].slice(0, 10);
        setRecentSearches(updated);
    };

    const handleSearch = (searchQuery = query) => {
        const q = searchQuery.trim();
        if (!q) {
            setError('Please enter a search query');
            return;
        }
        setError('');
        saveSearch(q);
        setShowSuggestions(false);

        if (onSearch) {
            onSearch(q);
        } else {
            navigate(`/search?q=${encodeURIComponent(q)}`);
        }
    };

    const handleChange = (e) => {
        const value = e.target.value;
        if (value.length <= MAX_LENGTH) {
            setQuery(value);
            setError('');
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Escape') {
            setShowSuggestions(false);
            setSelectedIndex(-1);
        } else if (e.key === 'ArrowDown') {
            e.preventDefault();
            setSelectedIndex(prev =>
                prev < suggestions.length - 1 ? prev + 1 : prev
            );
        } else if (e.key === 'ArrowUp') {
            e.preventDefault();
            setSelectedIndex(prev => prev > 0 ? prev - 1 : -1);
        } else if (e.key === 'Enter') {
            if (selectedIndex >= 0 && suggestions[selectedIndex]) {
                handleSearch(suggestions[selectedIndex]);
            } else {
                handleSearch();
            }
        }
    };

    const handleSuggestionClick = (suggestion) => {
        setQuery(suggestion);
        handleSearch(suggestion);
    };

    return (
        <div className={`search-bar-wrapper ${size}`}>
            <div className={`search-bar ${showSuggestions && suggestions.length > 0 ? 'has-suggestions' : ''}`}>
                <div className="search-icon">
                    <svg viewBox="0 0 24 24" width="20" height="20">
                        <path fill="#9aa0a6" d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
                    </svg>
                </div>

                <input
                    ref={inputRef}
                    type="text"
                    className="search-input"
                    value={query}
                    onChange={handleChange}
                    onKeyDown={handleKeyDown}
                    onFocus={() => setShowSuggestions(true)}
                    onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
                    placeholder=""
                    aria-label="Search"
                    maxLength={MAX_LENGTH}
                    autoComplete="off"
                />

                <div className="search-icons-right">
                    {query && (
                        <button
                            className="clear-btn"
                            onClick={() => setQuery('')}
                            aria-label="Clear search"
                        >
                            âœ•
                        </button>
                    )}
                    <span className="char-counter">{query.length}/{MAX_LENGTH}</span>
                    <button className="voice-btn" aria-label="Voice search">
                        <svg viewBox="0 0 24 24" width="24" height="24">
                            <path fill="#4285f4" d="M12 14c1.66 0 3-1.34 3-3V5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3z" />
                            <path fill="#ea4335" d="M17 11c0 2.76-2.24 5-5 5s-5-2.24-5-5H5c0 3.53 2.61 6.43 6 6.92V21h2v-3.08c3.39-.49 6-3.39 6-6.92h-2z" />
                        </svg>
                    </button>
                    <button className="camera-btn" aria-label="Search by image">
                        <svg viewBox="0 0 24 24" width="24" height="24">
                            <path fill="#34a853" d="M9.5 6.5v3h-3v-3h3M11 5H5v6h6V5zm-1.5 9.5v3h-3v-3h3M11 13H5v6h6v-6zm6.5-6.5v3h-3v-3h3M19 5h-6v6h6V5zm-6 8h1.5v1.5H13V13zm1.5 1.5H16V16h-1.5v-1.5zM16 13h1.5v1.5H16V13zm-3 3h1.5v1.5H13V16zm1.5 1.5H16V19h-1.5v-1.5zM16 16h1.5v1.5H16V16zm1.5-1.5H19V16h-1.5v-1.5zm0 3H19V19h-1.5v-1.5zM19 13h-1.5v1.5H19V13z" />
                        </svg>
                    </button>
                </div>

                {showSuggestions && suggestions.length > 0 && (
                    <ul className="suggestions" role="listbox">
                        {suggestions.map((suggestion, index) => (
                            <li
                                key={suggestion}
                                className={`suggestion ${index === selectedIndex ? 'selected' : ''}`}
                                onClick={() => handleSuggestionClick(suggestion)}
                                role="option"
                                aria-selected={index === selectedIndex}
                            >
                                <svg viewBox="0 0 24 24" width="16" height="16">
                                    <path fill="#9aa0a6" d="M13 3a9 9 0 0 0-9 9H1l3.89 3.89.07.14L9 12H6c0-3.87 3.13-7 7-7s7 3.13 7 7-3.13 7-7 7c-1.93 0-3.68-.79-4.94-2.06l-1.42 1.42A8.954 8.954 0 0 0 13 21a9 9 0 0 0 0-18z" />
                                </svg>
                                <span>{suggestion}</span>
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            {showError && error && (
                <p className="search-error" role="alert">{error}</p>
            )}
        </div>
    );
};

export default SearchBar;
