import React from 'react';
import './ImageGrid.css';

// Generate deterministic image results using Picsum
const generateImageResults = (query, count = 12) => {
    const results = [];
    const seed = query.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);

    for (let i = 0; i < count; i++) {
        const imageId = ((seed + i * 17) % 1000) + 1;
        const width = 300 + (i % 3) * 50;
        const height = 200 + (i % 4) * 30;

        results.push({
            id: `img-${i}-${seed}`,
            url: `https://picsum.photos/seed/${query.replace(/\s/g, '')}-${i}/${width}/${height}`,
            thumbnail: `https://picsum.photos/seed/${query.replace(/\s/g, '')}-${i}/300/200`,
            title: `${query} - Image ${i + 1}`,
            source: 'picsum.photos',
            width,
            height,
        });
    }

    return results;
};

const ImageGrid = ({ query, onImageClick }) => {
    const images = generateImageResults(query, 15);

    return (
        <div className="image-grid" role="region" aria-label="Image results">
            {images.map((image, index) => (
                <div
                    key={image.id}
                    className="image-item"
                    onClick={() => onImageClick?.(image)}
                    role="button"
                    tabIndex={0}
                    aria-label={image.title}
                    onKeyPress={(e) => e.key === 'Enter' && onImageClick?.(image)}
                >
                    <div className="image-wrapper">
                        <img
                            src={image.thumbnail}
                            alt={image.title}
                            loading="lazy"
                        />
                    </div>
                    <div className="image-info">
                        <span className="image-source">{image.source}</span>
                        <span className="image-title">{image.title}</span>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default ImageGrid;
